EditText:
(R.id.et_nombre)
        val correoEditText = findViewById(R.id.et_correo)
        val telefonoEditText = findViewById(R.id.et_telefono)
        val imageView = findViewById(R.id.imageView)
        val enviarButton = findViewById(R.id.btn_enviar)

        enviarButton.setOnClickListener {
            val nombre = nombreEditText.text.toString()
            val correo = correoEditText.text.toString()
            val telefono = telefonoEditText.text.toString()

            // Validar que todos los campos estén llenos
            if (nombre.isEmpty() || correo.isEmpty() || telefono.isEmpty()) {
                Toast.makeText(this, "Por favor, complete todos los campos", Toast.LENGTH_SHORT).show()
            } else {
                // Lógica para cambiar la imagen de perfil
                imageView.setImageResource(R.drawable.new_image)
                Toast.makeText(this, "Formulario enviado correctamente", Toast.LENGTH_SHORT).show()
            }
        }
    }
}
